.player-full-win:not(.danmaku-send-bar-unloaded) .live-web-player-controller .control-area .danmaku-send-bar {
    display: flex;
    margin: 0 24px;
    flex: 1 1 0;
    height: 24px;
    justify-content: center;
    align-items: center;
}
.player-full-win:not(.danmaku-send-bar-unloaded) .live-web-player-controller .control-area .danmaku-send-bar input {
    outline: none !important;
    border: none;
    border-bottom: 2px solid #fff8;
    background-color: transparent;
    color: #fff;
    padding: 4px;
    line-height: normal;
    flex: 1;
    width: 0;
    max-width: 400px;
    min-width: 70px;
}
.player-full-win:not(.danmaku-send-bar-unloaded) .live-web-player-controller .control-area .danmaku-send-bar input:focus-within {
    border-color: var(--theme-color);
}
.player-full-win:not(.danmaku-send-bar-unloaded) .live-web-player-controller .control-area .danmaku-send-bar input::-webkit-input-placeholder {
    color: #fff8 !important;
}
.player-full-win:not(.danmaku-send-bar-unloaded) .live-web-player-controller .control-area .right-area {
    flex: 0 0 auto !important;
}
